{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyOKNiH+IOXdKobkLcJc7W48"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"VOHDzbGg9Y7X"},"outputs":[],"source":["### 安裝函式庫 ###\n","from os\n","from flask import Flask, request, abort\n","\n","from linebot.v3 import WebhookHandler\n","from linebot.v3.exceptions import InvalidSignatureError\n","from linebot.v3.webhooks import MessageEvent, TextMessageContent\n","from linebot.v3.messaging import (\n","    Configuration, ApiClient, MessagingApi,\n","    ReplyMessageRequest,\n","    TextMessage\n",")\n","import requests\n","\n","from linebot.v3 import WebhookHandler\n","from linebot.v3.exceptions import InvalidSignatureError\n","from linebot.v3.webhooks import MessageEvent, TextMessageContent\n","from linebot.v3.messaging import (\n","    Configuration, ApiClient, MessagingApi,\n","    ReplyMessageRequest,\n","    TextMessage,\n","    TemplateMessage, ConfirmTemplate, MessageAction,\n","    CarouselTemplate, CarouselColumn, URIAction, PostbackAction\n",")\n","\n","import json\n","import random\n","\n","\n","\n","### Crawler ###\n","\n","def crawler(search_word):\n","  url = \"https://taigitube.com/api/search\"\n","\n","  headers = {\n","      \"User-Agent\": \"Mozilla/5.0\",\n","      \"Accept\": \"application/json\",\n","      \"Content-Type\": \"application/json\",\n","      \"Origin\": \"https://taigitube.com\",\n","      \"Referer\": \"https://taigitube.com/\",\n","  }\n","\n","  # 注意：只需要這三個欄位（過多反而會錯）\n","  payload = {\n","      \"query\": search_word,  # 搜尋關鍵字\n","      \"v\": \"\",          # 可以留空\n","      \"t\": \"0\"          # 通常是影片時間篩選參數\n","  }\n","\n","  response = requests.post(url, headers=headers, json=payload).json()\n","\n","  reply = []\n","  # 如果 response 超過10個就隨機取出10個 (LineBot templete 最多只能10個)\n","  if len(response) > 10:\n","    response = random.sample(response, 10)\n","\n","  for r in response:\n","    #print(r['video_name'])\n","    #print(r['transcript'])\n","    yt_url = 'https://youtu.be/watch?v=' + str(r['video_id']) + '&t=' + str(r['start_second']) + 's'\n","    photo_url = 'https://i.ytimg.com/vi/' + str(r['video_id']) + '/hqdefault.jpg'\n","    #print(yt_url)\n","    #print(photo_url)\n","\n","    reply.append([r['video_name'], r['transcript'], yt_url, photo_url])\n","\n","\n","  return reply\n","\n","\n","\n","### Line Bot ###\n","\n","app = Flask(__name__)\n","\n","configuration = Configuration(access_token=os.getenv('LINE_CHANNEL_ACCESS_TOKEN'))\n","line_handler = WebhookHandler(os.getenv('LINE_CHANNEL_SECRET'))\n","\n","@app.route('/')\n","def index():\n","    return 'TaigiTube LINE Bot is running!'\n","\n","@app.route(\"/callback\", methods=['POST'])\n","def callback():\n","    signature = request.headers['X-Line-Signature']\n","    body = request.get_data(as_text=True)\n","    try:\n","        line_handler.handle(body, signature)\n","    except InvalidSignatureError:\n","        abort(400)\n","    return 'OK'\n","\n","\n","@line_handler.add(MessageEvent, message=TextMessageContent)\n","def handle_message(event):\n","    with ApiClient(configuration) as api_client:\n","        line_bot_api = MessagingApi(api_client)\n","\n","\n","        data = crawler(event.message.text)\n","        print(data)\n","\n","        template_list = []\n","        for d in data:\n","            template_list.append(\n","                CarouselColumn(\n","                    thumbnail_image_url = d[3],\n","                    title = d[0],\n","                    text = d[1],\n","                    actions=[\n","                        URIAction(label = '「' + d[0] + '」的「' + event.message.text + '」怎麼講', uri = d[2])\n","                    ]\n","                )\n","            )\n","        reply = TemplateMessage(\n","            alt_text = '「' + event.message.text + '」怎麼講',\n","            template = CarouselTemplate(columns = template_list)\n","        )\n","\n","\n","        line_bot_api.reply_message(\n","            ReplyMessageRequest(\n","                reply_token=event.reply_token,\n","                messages=[\n","                    reply\n","                ]\n","            )\n","        )\n","\n","\n","if __name__ == \"__main__\":\n","    app.run()"]}]}